package com.example.springedu.controller;

import com.example.springedu.domain.CountDTO;
import com.example.springedu.domain.LottoDTO;
import org.springframework.stereotype.Controller;
import org.springframework.ui.Model;
import org.springframework.web.bind.annotation.GetMapping;
import org.springframework.web.bind.annotation.ModelAttribute;
import org.springframework.web.bind.annotation.SessionAttributes;
import org.springframework.web.bind.support.SessionStatus;


import javax.servlet.http.HttpSession;

@Controller
@SessionAttributes("cnt")
public class lottoController {

    @ModelAttribute("cnt")
    public int[] createCnt(){
        return new int[1];
    }


    @GetMapping("/lotto")
    public String lotto(LottoDTO dto , Model model, @ModelAttribute("cnt")int arr[], SessionStatus s){
        String msg="";

        if(arr[0]> 2){
            dto.setResult("over");
            dto.setImgName("data:image/jpeg;base64,");
            msg="로또 응모는 낙첨된 경우에 한하여 3번 까지만 가능합니다.<br>브라우저를 재기동한 후에 응모해 주세요";

        }else{
            int ran = (int)((Math.random()*6)+1);
            if(dto.getLottoNum() == ran){
                dto.setResult("success");
                dto.setImgName("data:image/jpeg;base64,");
                msg="추카추카!!";
                s.setComplete();
            }else{
                dto.setResult("fail");
                dto.setImgName("data:image/jpeg;base64,");
                arr[0]++;
                msg="아쉽네요.. 다음 기회에!!";
            }
        }

        model.addAttribute("lotto",dto);
        model.addAttribute("msg",msg);

        return "lottoView";
    }
}

